import type { MedusaRequest, MedusaResponse } from "@medusajs/framework/http"
import { z } from "zod"
import { handleApiError } from "../../../lib/api-error-handler"

const createSchema = z.object({
  product_id: z.string().min(1),
  title: z.string().min(1),
  description: z.string().nullable().optional(),
  auction_type: z.enum(["english", "dutch", "sealed", "reserve"]),
  starting_price: z.number(),
  reserve_price: z.number().nullable().optional(),
  buy_now_price: z.number().nullable().optional(),
  currency_code: z.string().min(1),
  bid_increment: z.number(),
  starts_at: z.string().min(1),
  ends_at: z.string().min(1),
  auto_extend: z.boolean().optional(),
  extend_minutes: z.number().optional(),
  status: z.enum(["draft", "scheduled", "active", "ended", "cancelled"]).optional(),
  metadata: z.record(z.string(), z.unknown()).nullable().optional(),
})

export async function GET(req: MedusaRequest, res: MedusaResponse) {
  try {
    const vendorId = (req as any).vendor_id
    if (!vendorId) {
      return res.status(401).json({ message: "Vendor authentication required" })
    }

    const mod = req.scope.resolve("auction") as any
    const { limit = "20", offset = "0", status } = req.query as Record<string, string | undefined>

    const filters: Record<string, any> = { vendor_id: vendorId }
    if (status) filters.status = status

    const items = await mod.listAuctionListings(filters, {
      skip: Number(offset),
      take: Number(limit),
    })

    return res.json({
      items,
      count: Array.isArray(items) ? items.length : 0,
      limit: Number(limit),
      offset: Number(offset),
    })

  } catch (error: any) {
    handleApiError(res, error, "GET vendor auctions")}
}

export async function POST(req: MedusaRequest, res: MedusaResponse) {
  try {
    const vendorId = (req as any).vendor_id
    if (!vendorId) {
      return res.status(401).json({ message: "Vendor authentication required" })
    }

    const mod = req.scope.resolve("auction") as any
    const validation = createSchema.safeParse(req.body)
    if (!validation.success) {
      return res.status(400).json({ message: "Validation failed", errors: validation.error.issues })
    }

    const item = await mod.createAuctionListings({
      ...validation.data,
      vendor_id: vendorId,
    })

    return res.status(201).json({ item })

  } catch (error: any) {
    handleApiError(res, error, "POST vendor auctions")}
}

