import type { MedusaRequest, MedusaResponse } from "@medusajs/framework/http"
import { z } from "zod"

const createSchema = z.object({
  code: z.string().min(1),
  initial_value: z.number().min(0),
  currency_code: z.string().min(1),
  recipient_name: z.string().nullable().optional(),
  recipient_email: z.string().nullable().optional(),
  sender_name: z.string().nullable().optional(),
  sender_email: z.string().nullable().optional(),
  message: z.string().nullable().optional(),
  expires_at: z.string().nullable().optional(),
  is_active: z.boolean().optional(),
  metadata: z.record(z.string(), z.unknown()).nullable().optional(),
})

export async function GET(req: MedusaRequest, res: MedusaResponse) {
  const vendorId = (req as any).vendor_id
  if (!vendorId) {
    return res.status(401).json({ message: "Vendor authentication required" })
  }

  const mod = req.scope.resolve("promotionExt") as any
  const { limit = "20", offset = "0", status } = req.query as Record<string, string | undefined>

  const filters: Record<string, any> = { vendor_id: vendorId }
  if (status) filters.is_active = status === "active"

  const items = await mod.listGiftCardExts(filters, {
    skip: Number(offset),
    take: Number(limit),
    order: { created_at: "DESC" },
  })

  return res.json({
    items,
    count: Array.isArray(items) ? items.length : 0,
    limit: Number(limit),
    offset: Number(offset),
  })
}

export async function POST(req: MedusaRequest, res: MedusaResponse) {
  const vendorId = (req as any).vendor_id
  if (!vendorId) {
    return res.status(401).json({ message: "Vendor authentication required" })
  }

  const mod = req.scope.resolve("promotionExt") as any
  const validation = createSchema.safeParse(req.body)
  if (!validation.success) {
    return res.status(400).json({ message: "Validation failed", errors: validation.error.issues })
  }

  const item = await mod.createGiftCardExts({
    ...validation.data,
    remaining_value: validation.data.initial_value,
    vendor_id: vendorId,
  })

  return res.status(201).json({ item })
}
