import type { MedusaRequest, MedusaResponse } from "@medusajs/framework/http"
import { z } from "zod"
import { handleApiError } from "../../lib/api-error-handler"

const createSchema = z.object({
  title: z.string().min(1),
  description: z.string().optional(),
  status: z.enum(["scheduled", "live", "ended", "cancelled"]).optional(),
  stream_url: z.string().optional(),
  platform: z.enum(["internal", "instagram", "tiktok", "youtube", "facebook"]).optional(),
  scheduled_at: z.string().optional(),
  thumbnail_url: z.string().optional(),
  metadata: z.record(z.string(), z.unknown()).nullable().optional(),
})

export async function GET(req: MedusaRequest, res: MedusaResponse) {
  try {
    const vendorId = (req as any).vendor_id
    if (!vendorId) {
      return res.status(401).json({ message: "Vendor authentication required" })
    }

    const mod = req.scope.resolve("socialCommerce") as any
    const { limit = "20", offset = "0", status } = req.query as Record<string, string | undefined>

    const filters: Record<string, any> = { host_id: vendorId }
    if (status) filters.status = status

    const items = await mod.listLiveStreams(filters, {
      skip: Number(offset),
      take: Number(limit),
      order: { created_at: "DESC" },
    })

    return res.json({
      items,
      count: Array.isArray(items) ? items.length : 0,
      limit: Number(limit),
      offset: Number(offset),
    })

  } catch (error) {
    handleApiError(res, error, "GET vendor social-commerce")
  }
}

export async function POST(req: MedusaRequest, res: MedusaResponse) {
  try {
    const vendorId = (req as any).vendor_id
    if (!vendorId) {
      return res.status(401).json({ message: "Vendor authentication required" })
    }

    const mod = req.scope.resolve("socialCommerce") as any
    const validation = createSchema.safeParse(req.body)
    if (!validation.success) {
      return res.status(400).json({ message: "Validation failed", errors: validation.error.issues })
    }

    const item = await mod.createLiveStreams({
      ...validation.data,
      host_id: vendorId,
    })

    return res.status(201).json({ item })

  } catch (error) {
    handleApiError(res, error, "POST vendor social-commerce")
  }
}
