// @ts-nocheck
import { createFileRoute } from "@tanstack/react-router"
import { useQuery } from "@tanstack/react-query"
import { sdk } from "@/lib/utils/sdk"
import { useAuth } from "@/lib/context/auth-context"
import { useState, useMemo } from "react"

interface SubscriptionPlan {
  id: string
  name: string
  handle: string
  description?: string
  billing_interval: string
  currency_code: string
  price: number
  compare_at_price?: number
  trial_period_days?: number
  features?: string[]
  status: string
  subscriber_count?: number
  created_at: string
}

export const Route = createFileRoute("/$tenant/$locale/vendor/subscriptions")({
  component: VendorSubscriptionsRoute,
})

function VendorSubscriptionsRoute() {
  const auth = useAuth()
  const [statusFilter, setStatusFilter] = useState<string>("")

  const vendorId = useMemo(() => {
    const user = (auth as any)?.user || (auth as any)?.customer
    if (user?.vendor_id) return user.vendor_id
    if (user?.metadata?.vendor_id) return user.metadata.vendor_id
    if (user?.id) return user.id
    return "current-vendor"
  }, [auth])

  const { data, isLoading } = useQuery({
    queryKey: ["vendor-subscriptions", statusFilter],
    queryFn: async () => {
      const params = new URLSearchParams()
      if (statusFilter) params.set("status", statusFilter)
      const url = `/vendor/subscriptions${params.toString() ? `?${params}` : ""}`
      return sdk.client.fetch<{ items: SubscriptionPlan[]; count: number }>(url, {
        credentials: "include",
      })
    },
  })

  const items = data?.items || []

  const statusColors: Record<string, string> = {
    active: "bg-ds-success/15 text-ds-success",
    draft: "bg-ds-muted text-ds-foreground",
    archived: "bg-ds-destructive/15 text-ds-destructive",
  }

  if (isLoading) {
    return (
      <div className="container mx-auto py-12">
        <div className="space-y-4">
          {[...Array(3)].map((_, i) => (
            <div key={i} className="border rounded-lg p-6 animate-pulse">
              <div className="h-4 bg-muted rounded w-1/3 mb-2" />
              <div className="h-3 bg-muted rounded w-1/2" />
            </div>
          ))}
        </div>
      </div>
    )
  }

  return (
    <div className="container mx-auto py-12">
      <div className="flex items-center justify-between mb-8">
        <h1 className="text-2xl font-bold">Subscription Plans</h1>
        <button className="px-4 py-2 bg-ds-primary text-white rounded-lg hover:bg-ds-primary/90 transition">
          + Create Plan
        </button>
      </div>

      <div className="flex gap-2 mb-6">
        {["", "active", "draft", "archived"].map((s) => (
          <button
            key={s}
            onClick={() => setStatusFilter(s)}
            className={`px-3 py-1.5 text-sm rounded-full border transition ${
              statusFilter === s ? "bg-ds-primary text-white border-ds-primary" : "bg-ds-card hover:bg-ds-muted/50"
            }`}
          >
            {s || "All"}
          </button>
        ))}
      </div>

      {items.length === 0 ? (
        <div className="text-center py-16 text-ds-muted-foreground">
          <p className="text-lg mb-2">No subscription plans yet</p>
          <p className="text-sm">Create your first subscription plan to get started.</p>
        </div>
      ) : (
        <div className="grid gap-4">
          {items.map((plan) => (
            <div key={plan.id} className="border rounded-lg p-6 hover:shadow-md transition">
              <div className="flex items-start justify-between">
                <div className="flex-1">
                  <div className="flex items-center gap-3 mb-2">
                    <h3 className="text-lg font-semibold">{plan.name}</h3>
                    <span className={`px-2 py-0.5 text-xs rounded-full font-medium ${statusColors[plan.status] || "bg-ds-muted text-ds-foreground"}`}>
                      {plan.status}
                    </span>
                  </div>
                  {plan.description && (
                    <p className="text-ds-muted-foreground text-sm mb-3">{plan.description}</p>
                  )}
                  <div className="flex items-center gap-6 text-sm text-ds-muted-foreground">
                    <span className="font-medium text-ds-foreground">
                      {plan.currency_code?.toUpperCase()} {(plan.price / 100).toFixed(2)}
                      <span className="text-ds-muted-foreground font-normal"> / {plan.billing_interval}</span>
                    </span>
                    {plan.trial_period_days && (
                      <span>{plan.trial_period_days}-day trial</span>
                    )}
                    {plan.subscriber_count !== undefined && (
                      <span>{plan.subscriber_count} subscribers</span>
                    )}
                  </div>
                  {plan.features && plan.features.length > 0 && (
                    <div className="flex flex-wrap gap-1.5 mt-3">
                      {plan.features.slice(0, 3).map((f, i) => (
                        <span key={i} className="px-2 py-0.5 bg-ds-muted text-xs rounded">{f}</span>
                      ))}
                      {plan.features.length > 3 && (
                        <span className="text-xs text-ds-muted-foreground/70">+{plan.features.length - 3} more</span>
                      )}
                    </div>
                  )}
                </div>
                <button className="text-sm text-ds-primary hover:underline ml-4">
                  View Details
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  )
}
